package com.gentech.loops;

public class Loopsdemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		do {
			System.out.println(i);
			i++;
		}while(i<=20);
		System.out.println("_--------------");
		
		//reverse order
		int j=20;
		do {
			System.out.println(j);
			j--;
		}while(j>=1);
	}

}
